import React, {Component} from 'react';
import {Route, Link} from 'react-router-dom';
// import MainPage from '../mainPage/mainPage';

class Registration extends Component {
  constructor(){
    super();
    this.state={
      open: false,
      username: "",
      password: "",
      firstName: "",
      lastName: "",
      email: "",
      errors: []
    }
    this.togglePanel =  this.togglePanel.bind(this);
  }
  togglePanel(e){
    this.setState({open: !this.state.open})
  }
  handleChange = event => {
  this.setState({
    [event.target.name]: event.target.value
  });
}
handleSubmit = async e =>{
  e.preventDefault();
  const {username, password, email } = this.state;
  const errors = validate(username, password, email);
    if(errors.length > 0){
      this.setState({errors});
      return;
    }
  const response = await fetch('/api/login/registration', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({username: this.state.username, password: this.state.password, firstName: this.state.firstName, lastName: this.state.lastName, email: this.state.email}),
  });
  this.setState({
    open: false,
    username: '',
    password: '',
    firstName: '',
    lastName: '',
    email: ''
  });
};
  render(){
    const {errors} = this.state;
    return(
      <div className='registrationBoard'>
        <button className='registration-btn reg-draw-border' onClick={(e) => this.togglePanel(e)}>New Storyteller</button>
        <br />
        {this.state.open ? (
          <form className='registrationForm' onSubmit={this.handleSubmit}>
            {errors.map(error => (
              <p key={error}> Error: {error}</p>
            ))}
            <input className='username reg-box' type='text' placeholder='Create your username' name='username' value={this.state.username} onChange={e => this.setState({username: e.target.value})}/>
            <br />
            <input className='password reg-box' type='text' placeholder='Create your password' name='password' value={this.state.password} onChange={e => this.setState({password: e.target.value})}/>
            <br />
            <input className='firstName reg-box' type='text' placeholder='Your first name?' name='firstName' value={this.state.firstName} onChange={e => this.setState({firstName: e.target.value})}/>
            <br />
            <input className='lastName reg-box' type='text' placeholder='Your last name?' name='lastName' value={this.state.lastName} onChange={e => this.setState({lastName: e.target.value})}/>
            <br />
            <input className='email reg-box' type='email' placeholder='Your Email?' name='email' value={this.state.email} onChange={e => this.setState({email: e.target.value})}/>
            <br />
            <button type='submit' className='register-btn reg-draw-border'>Register</button>
          </form>
        ): null}
      </div>
    );
  }
}

