import React, {Component} from 'react';
import {Tab, Tabs, TabList, TabPanel} from 'react-tabs';
import {NavLink} from 'react-router-dom';
import EditModal from './editModal';
import DeleteSvg from '../SvgIcons/times-circle-solid.svg';
import EditSvg from '../SvgIcons/keyboard-solid.svg';
import WindowCloseSvg from '../SvgIcons/window-close-regular.svg';
import swal from 'sweetalert';
import "./atEnd.css";

class TheEnd extends Component{
  state = {
    show: false,
    activeChapterNum: '',
    activeTitle: '',
    activeContent: '',
    id: '',
    chapterNum: '',
    title: '',
    content: ''
  };
  openEditModal = e => {
    this.setState({
      show: !this.state.show,
    });
  };

  componentDidMount(){
    swal({
    title: "Wrap Up?",
    text: "This page is where you do your delete, edit, learn about subconscious stimulations, and or even go back. Delete and Edit your chapters is in bottom box. Education area and next action is in top tabs.",
    icon: "info",
     button: "I understand!",
  });
};
async deleteChapter(chapterlist){
  swal({
  title: "Double Checking - Are you sure?",
  text: "Once deleted, " + `${chapterlist.title}` + " chapter will be POOF for good!" ,
  icon: "warning",
  buttons: true,
  dangerMode: true,
})
.then((willDelete) => {
  if (willDelete) {
    swal("It is done! Another chapter victim has fallen to the Great Trash!", {
      icon: "success",
    });
