import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import Registration from './registrationPage';
import OpenEye from '../SvgIcons/eye-regular.svg';
import CloseEye from '../SvgIcons/eye-slash-regular.svg';

class Login extends Component {
  constructor(){
    super();
    this.state={
      passwordIsMasked: true,
      loginUsername: '',
      loginPw: ''
    }
  }
  togglePeek = (e) =>{
    e.preventDefault();
    this.setState(prevState => ({ passwordIsMasked: !prevState.passwordIsMasked}));
};
handleSubmit = async e =>{
  e.preventDefault();
  const response = await fetch('/api/auth/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({loginUsername: this.state.loginUsername, loginPw: this.state.loginPw}),
  });
  console.log(`${this.state.loginUsername} + ' ' + ${this.state.loginPw}`);
};
  render(){
    const {passwordIsMasked} = this.state;
    return(
      <div>
        <form onSubmit={this.handleSubmit}>
          <input className='loginUsername' type='text' placeholder='Enter your username' value={this.state.loginUsername} onChange={e => this.setState({loginUsername: e.target.value})}/>
          <input className='loginPw' type={passwordIsMasked ? 'password' : 'text'} placeholder='Enter your password' value={this.state.loginPw} onChange={e => this.setState({loginPw: e.target.value})}/>
          <button className='togglePeek'onClick={this.togglePeek} ><img src={OpenEye} /></button>
          <br/>
          <Link to={'/MainPage/ShortIntro'}><button type='submit' className='login-btn login-draw-border'>Enter</button></Link>
        </form>
        <Registration />
      </div>
    );
  }
}

export default Login;
