import React, {Component} from 'react';
import {Route, Link, Switch} from 'react-router-dom';
// import hexIcon from '../hexagon.png';
import './mainPage.css';
import {Tab, Tabs, TabList, TabPanel} from 'react-tabs';
import SettingChoices from './settingChoices';
import Intro from './intro';
import createTheStory from '../createTheStory/createTheStory';
import TheEnd from '../atEnd/atEnd';
import TheList from '../createTheStory/totalProgress';
import SuggestionsList from './ideaSuggestions';
import shuffle from 'shuffle-array';
// import 'react-tabs/style/react-tabs.css';

class TheMain extends Component {
  constructor(props){
    super(props);
    this.state = {
      chapterlists: []
    };
  }
  componentDidMount() {
    fetch('/api/ChaptersForStory/all')
    .then(response => response.json())
    .then(data => this.setState({chapterlists: data.chapterlists}));
  }
  rotationSuggestions(e){
    shuffle(suggestions);
  }
  render(){
    const {chapterlists} = this.state;
    return (
      <div className='MainDashboard'>
        <section className='progressBar'>
          <div className='chapterStories'>
            Progress
          </div>
          <TheList chapterlists={chapterlists} />
        </section>
        <section className='StoryMakingContainer'>
          <Switch>
            <Route path='/MainPage/ShortIntro' component={Intro} onClick={this.rotationSuggestions.bind()}/>
            <Route path='/MainPage/Choices' component={SettingChoices} onClick={this.rotationSuggestions.bind()}/>
            <Route path='/MainPage/CreateChapter' component={createTheStory} onClick={this.rotationSuggestions.bind()}/>
            <Route path='/MainPage/WrapItUp' render={(props) => <TheEnd {...props} chapterlists={chapterlists} />} onClick={this.rotationSuggestions.bind()} />
          </Switch>
        </section>
        <section className='ideaSuggestionsBar'>
          <div className='boxOfSuggestions'>
            ??? List of Ideas ???
          </div>
          <SuggestionsList />
        </section>
      </div>
    );
  }
}

export default TheMain;
