import React, {Component} from 'react';
import {Route, Link} from 'react-router-dom';
// import MainPage from '../mainPage/mainPage';

class Registration extends Component {
  constructor(){
    super();
    this.state={
      open: false,
      username: "",
      password: "",
      firstName: "",
      lastName: "",
      email: ""
    }
    this.togglePanel =  this.togglePanel.bind(this);
  }
  togglePanel(e){
    this.setState({open: !this.state.open})
  }
  handleChange = event => {
  this.setState({
    [event.target.name]: event.target.value
  });
}
handleSubmit = async e =>{
  e.preventDefault();
  const response = await fetch('/login/registration', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({username: this.state.username, firstName: this.state.firstName, lastName: this.state.lastName, email: this.state.email}),
  });
  const body = await response.text();
  this.setState({responseToPost: body});
  console.log(this.state.responseToPost);
// on bottom of html list <p>{this.state.responseToPost}</p>
};
  render(){
    return(
      <div className='registrationBoard'>
        <button className='registration-btn reg-draw-border' onClick={(e) => this.togglePanel(e)}>New Storyteller</button>
        <br />
        {this.state.open ? (
          <form className='registrationForm' onSubmit={this.handleSubmit}>
            <input className='username reg-box' type='text' placeholder='Create your username' name='username' value={this.state.username} onChange={this.handleChange}/>
            <br />
            <input className='password reg-box' type='text' placeholder='Create your password' name='password' value={this.state.password} onChange={this.handleChange}/>
            <br />
            <input className='firstName reg-box' type='text' placeholder='Your first name?' name='firstName' value={this.state.firstName} onChange={this.handleChange}/>
            <br />
            <input className='lastName reg-box' type='text' placeholder='Your last name?' name='lastName' value={this.state.lastName} onChange={this.handleChange}/>
            <br />
            <input className='email reg-box' type='email' placeholder='Your Email?' name='email' value={this.state.email} onChange={this.handleChange}/>
            <br />
            <button type='submit' className='register-btn reg-draw-border'>Register</button>
          </form>
        ): null}
      </div>
    );
  }
}

        // <Link to={'/'}><button className='login-btn login-draw-border'>Back to Login</button></Link>

export default Registration;
