import React, {Component} from 'react';
import {Route, Link} from 'react-router-dom';
// import MainPage from '../mainPage/mainPage';

class Registration extends Component {
  constructor(){
    super();
    this.state={
      open: false,
      username: "",
      password: "",
      firstName: "",
      lastName: "",
      email: ""
    }
    this.togglePanel =  this.togglePanel.bind(this);
  }
  togglePanel(e){
    this.setState({open: !this.state.open})
  }
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  handleSubmit(e){
    e.preventDefault()
    this.props.storytellerRegister(this.state)
  }
  render(){
    return(
      <div className='registrationBoard'>
        <button className='registration-btn reg-draw-border' onClick={(e) => this.togglePanel(e)}>Join Up!</button>
        <br />
        {this.state.open ? (
          <form className='registrationForm' onSubmit={this.handleSubmit}>
            <p>New StoryTeller</p>
            <input className='username reg-box' type='text' placeholder='Create your username' />
            <br />
            <input className='password reg-box' type='text' placeholder='Create your password' />
            <br />
            <input className='firstName reg-box' type='text' placeholder='Your first name?' />
            <br />
            <input className='lastName reg-box' type='text' placeholder='Your last name?' />
            <br />
            <input className='email reg-box' type='text' placeholder='Your Email?' />
            <br />
            <button type='submit' className='register-btn reg-draw-border'>Register</button>
          </form>
        ): null}
      </div>
    );
  }
}

        // <Link to={'/'}><button className='login-btn login-draw-border'>Back to Login</button></Link>

export default Registration;
