import React, {Component} from 'react';
import TheList from '../createTheStory/totalProgress';


class StoryplotDropdown extends Component{
  constructor(){
    super();
    this.state ={
      displayPlot: false,
    };
    this.showDropdownPlot = this.showDropdownPlot.bind(this);
    this.hideDropdownPlot = this.hideDropdownPlot.bind(this);
  };
  showDropdownPlot(e){
    e.preventDefault();
    this.setState({displayPlot: true}, () => {
      document.addEventListner('click', this.hideDropdownPlot);
    });
  }
  hideDropdownPlot(e){
    e.preventDefault();
    this.setState({displayPlot: false}, () => {
      document.addEventListner('click', this.showDropdownPlot);
    });
  }

  render(){
    return (
      <div class="dropdown-bar mobile-progressBar">
        <button onClick={this.showDropdownPlot} class="dropbtn">Storyline</button>
        {this.state.displayPlot ? (
          <div class="dropdown-progress">
            <TheList chapterlists={chapterlists} />
          </div>
        ):
        (null)
      }
      </div>
    );
  }
}

export default StoryplotDropdown;
