      <div>
        <NavLink to={'/MainPage/Choices'} activeClassName='previousPage' ><button className='goPrevious modal-btn'><img className='backIcon'src={BackButton} /></button></NavLink>
        <img className='BackgroundImageSetting' src={this.props.location.state.referrer} alt='various background image'/>
