import React, {Component} from 'react';
import {Route, Switch} from 'react-router-dom';
import TheMain from './mainPage/mainPage';
import Welcome from './welcome/welcome';
import Registration from './welcome/registrationPage';
import Particles from 'react-particles-js';
import './App.css';
import ReactDOM from 'react-dom';
import { transitions, positions, Provider as AlertProvider } from 'react-alert';
import AlertTemplate from 'react-alert-template-basic';

const options = {
  // you can also just use 'bottom center'
  position: positions.BOTTOM_CENTER,
  timeout: 5000,
  offset: '30px',
  // you can also just use 'scale'
  transition: transitions.SCALE
}

// <Route path='/FinalizeStoryAndStats' component={} />
// <Route component={NotFoundPage} />

class App extends Component {
  render(){
  return (
    <AlertProvider template={AlertTemplate} {...options}>
    <div className='App'>
      <header>
        <h1 className='TopBoard'>DraftBoard</h1>
      </header>
      <main className='mainFrameApp'>
        <Switch>
          <Route exact path='/' component={Welcome} />
          <Route path='/MainPage' component={TheMain} />
        </Switch>
      </main>
      <Particles className="Particles"
      params={{
         "particles": {
          "number": {
            "value": 90
           },
           "color": { "value": "#000000" },
           "line_linked": {
             "distance": 120,
             "opacity": .8,
             "width": 1.2},
          "size": {
            "value": 4
           }
           },
           "interactivity": {
              "events": {
                "onhover": {
                  "enable": true,
                  "mode": "repulse"
                  }
                 }
                }
              }} />
    </div>
     </AlertProvider>
  );
};
}

export default App;
